<?phpinclude_once('model/startup.php');include_once('model/M_Users.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Менеджеры.$mUsers = M_Users::Instance();// Очистка старых сессий.$mUsers->ClearSessions();// Текущий пользователь.$user = $mUsers->Get();// Если пользователь не зарегистрирован - отправляем на страницу регистрации.if ($user == null) {	header("Location: login.php");	die();}// Может ли пользователь смотерть контакты?if (!$mUsers->Can('VIEW_CONTACT')) {	die('Отказано в доступе');}?><!DOCTYPE html PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN"><html><head>	<title>Веб-Гуру</title>	<link rel="stylesheet" type="text/css" media="screen" href="theme/style.css" /></head><body>	<h1>Контактная информация сотрудников</h1>	<a href="index.php">Главная</a>	<ul>		<li>Иванов, (926) 123-45-67</li>		<li>Петров, (925) 112-83-83</li>		<li>Замуруев, (916) 523-15-61</li>		<li>Семенова, (903) 323-44-66</li>			</ul></body></html>