<?php//// Базовый контроллер сайта.//abstract class Base extends Controller {	protected $title;		    // заголовок страницы    protected $center_page;		// содержание страницы	//	// Конструктор.	//	function __construct() {        include_once('/controller/Startup.php');        include_once('/model/Articles.php');        include_once('/model/Functions.php');	}		protected function before() {        // установка параметров, подключение к БД, старт сессии        startup();        $this->title = 'PHP. Уровень 2';		$this->center_page = '';	}		//	// Генерация базового шаблонаы	//		public function render() {        $page = $this->Template('/view/Main.php', array(  'title' => $this->title,                                                            'center_page' => $this->center_page));        echo $page;	}	}