<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller {    private $mFunctions;	protected $title;		    // заголовок страницы    protected $center_page;		// содержание страницы	//	// Конструктор.	//	public function __construct() {        $this->mFunctions = M_Functions::Instance();        include_once('/model/M_Functions.php');	}		protected function before() {        // установка параметров, подключение к БД, старт сессии        $this->mFunctions->startup();        $this->title = 'PHP. Уровень 2';		$this->center_page = '';	}		//	// Генерация базового шаблонаы	//		public function render() {        $page = $this->Template('/view/V_Main.php', array(  'title' => $this->title,                                                            'center_page' => $this->center_page));        echo $page;	}	}?>