<?include_once('../controller/c_startup.php');include_once('../model/m_articles.php');include_once('../model/m_functions.php');// установка параметров, подключение к БД, старт сессииstartup();// Обработка отправки формы.if (!empty($_POST)) {    if ($_POST['subject'] == '' || $_POST['content'] == '') {                                     // не заполнено одно из полей        $subject = $_POST['subject'];        $content = $_POST['content'];        $empty_field = true;    }	elseif (!new_article($_POST['subject'], $_POST['content'])) die('что-то пошло не так!');     // не удалось записать в БД    else {                                                                                      // статья добавлена		header('Location: ../controller/c_index.php');		die();    }}else {	$subject = '';	$content = '';    $empty_field = false;}// Вывод в шаблон.$center_page = template('../view/v_new.php', array( 'subject' => $subject,                                                    'content' => $content,                                                    'empty_field' => $empty_field));$page = template('../view/v_main.php', array('center_page' => $center_page));echo $page;?>