<?php//// вспомогательные функции//class M_Functions {    private static $instance;    public static function Instance() {        if (self::$instance == null) self::$instance = new self;        return self::$instance;    }    private function __construct() {    }    //    // Короткое описание статьи    //    public function content_intro($content) {        if (mb_strlen($content) > MAX_SYMBOL_INTRO) {            $intro = explode(' ', mb_substr($content, 0, MAX_SYMBOL_INTRO, ENCODING));            array_pop($intro);            $intro = implode(' ', $intro) . '...';        }        else $intro = $content;        return $intro;    }    //    // экранирует одинарные кавычки, html-теги и лишние пробелы по краям    //    public function protect($str) {        return trim(htmlspecialchars(mysql_real_escape_string($str)));    }    public function startup() {        require_once('/config.php');        // подключение к БД        $link = mysql_connect(DB_HOST, DB_USER, DB_PASS) or die('<br />Не могу соединиться с MySQL:<br />' . mysql_error());        // коннект с MySQL        mysql_select_db(DB_NAME, $link) or die("<br />Не могу подключиться к базе " . DB_NAME . "<br />" . mysql_errno() . " - " . mysql_error() . "<br />");        // играем с кодировкой        mysql_set_charset(ENCODING, $link); 													                                    // установка кодировки соединения с БД        mb_internal_encoding(ENCODING);                                                                                             // установка внутренней кодировки скрипта        setlocale(LC_ALL, 'ru_RU.' . ENCODING);                                                                                     // кодировка исполнения php        session_start();                                                                                                            // старт сессии    }}?>