<?phpinclude_once('../controller/c_startup.php');include_once('../model/m_main.php');// установка параметров, подключение к БД, старт сессииstartup();// если только что была добавлена статьяif ($_SESSION['update_success']) {    unset($_SESSION['update_success']);    $update_success = true;}// если только что была удалена статьяif ($_SESSION['delete_success']) {    unset($_SESSION['delete_success']);    $delete_success = true;}// Извлечение статей.if (!$articles = articles_all()) die('что-то пошло не так!');// Вывод в шаблон.$center_page = template('../view/v_editor.php', array( 'articles' => $articles,                                                        'update_success' => $update_success,                                                        'delete_success' => $delete_success));$page = template('../view/v_main.php', array('center_page' => $center_page));echo $page;?>