<?phpinclude_once('../controller/c_startup.php');include_once('../model/m_main.php');// установка параметров, подключение к БД, старт сессииstartup();// если только что была добавлена статьяif ($_SESSION['add_success']) {    unset($_SESSION['add_success']);    $add_success = true;}// Извлечение статей.if (!$articles = articles_all()) die('что-то пошло не так!');// Вывод в шаблон.$center_page = template('../view/v_index.php', array(   'articles' => $articles,                                                        'add_success' => $add_success));$page = template('../view/v_main.php', array('center_page' => $center_page));echo $page;?>