<?include_once('startup.php');include_once('../model/m_main.php');// установка параметров, подключение к БД, старт сессииstartup();// Обработка отправки формы.if (!empty($_POST)) {    if ($_POST['subject'] == '' || $_POST['content'] == '') {                                     // не заполнено одно из полей        $subject = $_POST['subject'];        $content = $_POST['content'];        $empty_field = true;    }	elseif (!new_article($_POST['subject'], $_POST['content'])) die('что-то пошло не так!');     // не удалось записать в БД    else {                                                                                      // статья добавлена		header('Location: index.php');		die();    }}else {	$subject = '';	$content = '';    $empty_field = false;}// Вывод в шаблон.include('../view/v_new.php');?>