<?include_once('startup.php');include_once('model.php');// установка параметров, подключение к БД, старт сессииstartup();// Обработка отправки формы.if (!empty($_POST)) {    if ($_POST['subject'] == '' || $_POST['content'] == '') {                                                           // не заполнено одно из полей        $id_article = $_POST['id_article'];        $subject = $_POST['subject'];        $content = $_POST['content'];        $empty_field = true;    }    elseif (!edit_article($_POST['id_article'], $_POST['subject'], $_POST['content'])) die('что-то пошло не так!');        // не удалось записать в БД    else {                                                                                                              // статья добавлена        header('Location: editor.php');        die();    }}else {    // Извлечение статьи если есть её id    if (!isset($_GET['id_article'])) die('Не указан id статьи!');    elseif(!$article = get_article($_GET['id_article'])) die('что-то пошло не так!');    $id_article = $article['id_article'];    $subject = $article['subject'];    $content = $article['content'];    $empty_field = false;}// Вывод в шаблон.include('view/edit.php');?>