<?phpinclude_once('startup.php');include_once('model.php');// установка параметров, подключение к БД, старт сессииstartup();// если только что была добавлена статьяif ($_SESSION['update_success']) {    unset($_SESSION['update_success']);    $update_success = true;}// если только что была удалена статьяif ($_SESSION['delete_success']) {    unset($_SESSION['delete_success']);    $delete_success = true;}// Извлечение статей.if (!$articles = articles_all()) die('что-то пошло не так!');// Вывод в шаблон.include('view/editor.php');?>