<?php//// Список всех статей//function articles_all() {	$result = mysql_query("SELECT * FROM `articles` ORDER BY `articles`.`create_date` DESC;");	if (!$result) return false;		// Извлечение из БД.	while ($row = mysql_fetch_array($result)) {        $articles[] = $row;	}	return $articles;}//// Конкретная статья//function articles_get($id_article){	// TODO}//// Добавить статью//function articles_new($title, $content){	// Подготовка.	$title = trim($title);	$content = trim($content);	// Проверка.	if ($title == '')		return false;		// Запрос.	$t = "INSERT INTO articles (title, content) VALUES ('%s', '%s')";		$query = sprintf($t, 	                 mysql_real_escape_string($title),	                 mysql_real_escape_string($content));		$result = mysql_query($query);								if (!$result)		die(mysql_error());			return true;}//// Изменить статью//function articles_edit($id_article, $title, $content){	// TODO}//// Удалить статью//function articles_delete($id_article){	// TODO}//// Короткое описание статьи//function articles_intro($article){	// TODO	// $article - это ассоциативный массив, представляющий статью}