<?include_once('startup.php');include_once('model.php');// установка параметров, подключение к БД, старт сессииstartup();// Обработка отправки формы.if (!empty($_POST)) {    if ($_POST['title'] == '' || $_POST['content'] == '') {                                     // не заполнено одно из полей        $title = $_POST['title'];        $content = $_POST['content'];        $empty_field = true;    }	elseif (!new_article($_POST['title'], $_POST['content'])) die('что-то пошло не так!');     // не удалось записать в БД    else {                                                                                      // статья добавлена		header('Location: index.php');		die();    }}else {	$title = '';	$content = '';	$error = false;}// Вывод в шаблон.include('view/new.php');?>