<?include_once('startup.php');include_once('model.php');// установка параметров, подключение к БД, старт сессииstartup();// Обработка отправки формы.if (!empty($_POST)) {    if ($_POST['subject'] == '' || $_POST['context'] == '') {                                     // не заполнено одно из полей        $subject = $_POST['subject'];        $context = $_POST['context'];        $empty_field = true;    }	elseif (!new_article($_POST['subject'], $_POST['context'])) die('что-то пошло не так!');     // не удалось записать в БД    else {                                                                                      // статья добавлена		header('Location: index.php');		die();    }}else {	$subject = '';	$context = '';    $empty_field = false;}// Вывод в шаблон.include('view/new.php');?>