<?/*Шаблон главной страницы=======================$subject - заголовок$content - содержание*/?><div class="center">    <h1>Новая статья</h1>    <? if($empty_field): ?>        <b style="color: red;">Заполните все поля!</b>    <? endif; ?>    <form method="post">        Название:<br />        <input type="text" name="subject" value="<?=$subject;?>" /><br />        <br />        Содержание:<br />        <textarea name="content"><?=$content;?></textarea><br />        <input type="submit" value="Добавить" />    </form></div>